mapper.select:
SELECT t1.*, t2.category_name, t3.id as first_category_id, t3.category_name as first_category_name from rui_product t1 left join rui_category t2 on t1.category_id = t2.id left join rui_category t3 on t2.parent_id = t3.id where 1=1
and t1.product_name like concat('%', #{query.name}, '%')
limit #{page.startIndex}, #{page.pageSize}

mapper.update:
update rui_product set category_id=#{categoryId}, product_name=#{productName}, unit=#{unit},
bar_code=#{barCode}, product_code=#{productCode}, default_price=#{defaultPrice}, remark=#{remark}
where id=#{id}
